    private void ConvertCFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConvertCFActionPerformed
        // TODO add your handling code here:
        if(!Input.getText().isEmpty()){
            try{
                Double tCel = Double.valueOf(Input.getText());
                Double tFah = 1.8 * tCel + 32.0;
                Output.setText(String.format("%.6f", tFah));
            }catch(NumberFormatException e){
                Output.setText("No es un número");
            }
        }else{
            Output.setText("Sin datos");
        }
    }//GEN-LAST:event_ConvertCFActionPerformed

    private void InputKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_InputKeyPressed
        // TODO add your handling code here:
        if(evt.getKeyCode() == KeyEvent.VK_ENTER){
            ConvertCFActionPerformed(null);
        }
    }//GEN-LAST:event_InputKeyPressed

    private void OutputKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_OutputKeyPressed
        // TODO add your handling code here:
        if(evt.getKeyCode() == KeyEvent.VK_ENTER){
            ConvertFCActionPerformed(null);
        }
    }//GEN-LAST:event_OutputKeyPressed

