    private void formKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_formKeyPressed
        // TODO add your handling code here:
        if(evt.getKeyCode() == KeyEvent.VK_ESCAPE){
            System.exit(0);
        }
    }//GEN-LAST:event_formKeyPressed

    private void ConvertFCActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConvertFCActionPerformed
        // TODO add your handling code here:
        if(!Output.getText().isEmpty()){
            try{
                Double tFah = Double.valueOf(Output.getText());
                Double tCel = (tFah - 32.0)/1.8;
                Input.setText(String.format("%.6f", tCel));
            }catch(NumberFormatException e){
                Input.setText("No es un número");
            }
        }else{
            Input.setText("Sin datos");
        }
    }//GEN-LAST:event_ConvertFCActionPerformed

